import React, { Component } from 'react';
import LottoBall from './LottoBall';

class Lottery extends Component {
  static defaultProps = {
    title: 'Lotto',
    maxBall: 6,
    maxNum: 40
  };

  constructor(props) {
    super(props);
    this.state = {
      numbers: Array.from({ length: this.props.maxBall })
    };
    this.genRandNum = this.genRandNum.bind(this);
  }

  genRandNum() {
    let num1 = Math.floor(Math.random() * this.props.maxNum);

    // console.log(this.state.numbers);
  }

  render() {
    return (
      <div className="App">
        <h1>{this.props.title}</h1>
        <LottoBall />
        <button onClick={this.genRandNum}>Generate</button>
      </div>
    );
  }
}

export default Lottery;
