import React, { Component } from 'react';
import LottoBall from './LottoBall';

class Lottery extends Component {
  constructor() {
    super();
    this.state = {
      numbers: []
    };
    this.genRandNum = this.genRandNum.bind(this);
  }

  genRandNum() {
    // console.log(maxnum);
    let num1 = Math.floor(Math.random() * 40);
    let num2 = Math.floor(Math.random() * 40);
    let num3 = Math.floor(Math.random() * 40);
    let num4 = Math.floor(Math.random() * 40);
    let num5 = Math.floor(Math.random() * 40);
    let num6 = Math.floor(Math.random() * 40);

    this.setState({ numbers: [num1, num2, num3, num4, num5, num6] });
    // console.log(this.state.numbers);
  }

  render() {
    return (
      <div className="App">
        <h1>Lottery</h1>
        <LottoBall randNum={this.state.numbers[0]} />
        <LottoBall randNum={this.state.numbers[1]} />
        <LottoBall randNum={this.state.numbers[2]} />
        <LottoBall randNum={this.state.numbers[3]} />
        <LottoBall randNum={this.state.numbers[4]} />
        <LottoBall randNum={this.state.numbers[5]} />
        <br></br>
        <button onClick={this.genRandNum}>Generate</button>
      </div>
    );
  }
}

export default Lottery;
